 /**
  * @brief 以对话框方式间接创建控件
  * @note 无
  * @param 无
  * @retval hWin：资源表中第一个控件的句柄
  */
WM_HWIN CreateFramewin(void);
WM_HWIN CreateFramewin(void) {
  WM_HWIN hWin;

  hWin = GUI_CreateDialogBox(_aDialogCreate, GUI_COUNTOF(_aDialogCreate), _cbDialog, WM_HBKWIN, 0, 0);
  return hWin;
}

/**
  * @brief GUI主任务
  * @note 无
  * @param 无
  * @retval 无
  */
void MainTask(void)
{
	/* 设置背景色 */
	GUI_SetBkColor(GUI_GRAY_3F);
	GUI_Clear();
	/* 重定向背景窗口的回调函数 */
	WM_SetCallback(WM_HBKWIN, _cbBk);
	/* 间接创建窗口 */
	CreateFramewin();

	while (1)
	{
		GUI_Delay(20);
	}
}

